using UniSpy.Server.WebServer.Module.Sake.Abstraction;
using UniSpy.Server.WebServer.Module.Sake.Contract.Request;
using UniSpy.Server.WebServer.Module.Sake.Contract.Result;

namespace UniSpy.Server.WebServer.Module.Sake.Contract.Response
{
    internal class SearchForRecordResponse : ResponseBase
    {
        private new SearchForRecordsRequest _request => (SearchForRecordsRequest)base._request;
        private new SearchForRecordsResult _result => (SearchForRecordsResult)base._result;
        public SearchForRecordResponse(SearchForRecordsRequest request, SearchForRecordsResult result) : base(request, result)
        {
        }

        public override void Build()
        {
            _content.Add("values");
            _content.Add("ArrayOfRecordValue");
            foreach (var item in _result.UserData)
            {
                if (item.Key == "RecordValue")
                {
                    _content.Add("RecordValue");
                }
                _content.Add(item.Key, item.Value);
            }
            SendingBuffer = @"<?xml version=""1.0"" encoding=""UTF-8""?>
                                <SOAP-ENV:Envelope xmlns:SOAP-ENV=""http://schemas.xmlsoap.org/soap/envelope/""
                                    xmlns:SOAP-ENC=""http://schemas.xmlsoap.org/soap/encoding/""
                                    xmlns:xsi=""http://www.w3.org/2001/XMLSchema-instance""
                                    xmlns:xsd=""http://www.w3.org/2001/XMLSchema""
                                    xmlns:ns1=""http://gamespy.net/sake"">
                                    <SOAP-ENV:Body>
                                        <ns1:SearchForRecordsResponse>
                                            <ns1:SearchForRecordsResult>Success</ns1:SearchForRecordsResult>
                                            <ns1:values>
                                                <ns1:ArrayOfRecordValue>
                                                    <ns1:RecordValue>
                                                        <ns1:binaryDataValue>
                                                            <ns1:value></ns1:value>
                                                        </ns1:binaryDataValue>
                                                    </ns1:RecordValue>
                                                    <ns1:RecordValue>
                                                        <ns1:intValue>
                                                            <ns1:value>0</ns1:value>
                                                        </ns1:intValue>
                                                    </ns1:RecordValue>
                                                </ns1:ArrayOfRecordValue>
                                            </ns1:values>
                                        </ns1:SearchForRecordsResponse>
                                    </SOAP-ENV:Body>
                                </SOAP-ENV:Envelope>";
            // base.Build();
        }
    }
}
