        [Fact]
        public void XMLToJsonTest()
        {
            var data = @"<?xml version=""1.0"" encoding=""UTF-8""?>
                                <SOAP-ENV:Envelope xmlns:SOAP-ENV=""http://schemas.xmlsoap.org/soap/envelope/""
                                    xmlns:SOAP-ENC=""http://schemas.xmlsoap.org/soap/encoding/""
                                    xmlns:xsi=""http://www.w3.org/2001/XMLSchema-instance""
                                    xmlns:xsd=""http://www.w3.org/2001/XMLSchema""
                                    xmlns:ns1=""http://gamespy.net/sake"">
                                    <SOAP-ENV:Body>
                                        <ns1:SearchForRecordsResponse>
                                            <ns1:SearchForRecordsResult>Success</ns1:SearchForRecordsResult>
                                            <ns1:values>
                                                <ns1:ArrayOfRecordValue>
                                                    <ns1:RecordValue>
                                                        <ns1:binaryDataValue>
                                                            <ns1:value></ns1:value>
                                                        </ns1:binaryDataValue>
                                                    </ns1:RecordValue>
                                                    <ns1:RecordValue>
                                                        <ns1:intValue>
                                                            <ns1:value>0</ns1:value>
                                                        </ns1:intValue>
                                                    </ns1:RecordValue>
                                                </ns1:ArrayOfRecordValue>
                                            </ns1:values>
                                        </ns1:SearchForRecordsResponse>
                                    </SOAP-ENV:Body>
                                </SOAP-ENV:Envelope>";
            var xele = XElement.Parse(data);
            var array = xele.Descendants().Where(d => d.Name.LocalName == "ArrayOfRecordValue").First();
            var jsonText = Newtonsoft.Json.JsonConvert.SerializeXNode(array);
            jsonText = jsonText.Replace("ns1:", "");
            // var newJsonText = jsonText.Replace("{","<ns1:");
            var jsonDoc = System.Text.Json.JsonDocument.Parse(jsonText);
            // jsonDoc.RootElement.EnumerateArray().Where(n=>n.)
            jsonDoc.RootElement.ToString();

            var newXele = Newtonsoft.Json.JsonConvert.DeserializeXNode(jsonText).Root;
            var ns = Module.Sake.Contract.SakeSoapEnvelope.SakeNamespace;
            // Add a namespace declaration to the root element
            newXele.Add(new XAttribute(XNamespace.Xmlns + "ns1", ns));

            // Select the nodes that need the namespace
            // var recordValueNodes = newXele.Descendants(Module.Sake.Contract.SakeSoapEnvelope.SakeNamespace + "RecordValue");

            // Add the namespace to each selected node
            foreach (var childNode in newXele.DescendantsAndSelf())
            {
                foreach (var attribute in childNode.Attributes())
                {
                    attribute.Remove();
                    childNode.Add(new XAttribute(ns + attribute.Name.LocalName, attribute.Value));
                }
                foreach (var element in childNode.Elements())
                {
                    element.Name = ns + element.Name.LocalName;
                }
            }

            // Print the modified XML content
            string modifiedXml = newXele.ToString();
            // var xeleStr = newXele.ToString().Replace("<", "<ns1:");

        }
    }
